/***********************\
    Loader widget
\***********************/

body.silex-runtime {
    background-color: black;
    transition: background-color 0s .3s linear;
}

body.silex-runtime>* {
    opacity: .1;
    transition: opacity .3s ease-in;
}

body.silex-runtime.loaded>* {
    opacity: initial;
}

body.loaded {
    background-color: initial;
}

body.silex-runtime:before {
    content: '';
    border: 16px solid #f3f3f3;
    border-top: 16px solid #3498db;
    border-radius: 50%;
    width: 120px;
    height: 120px;
    animation: spin 2s linear infinite;
    position: absolute;
    top: 50vh;
    left: 50vw;
    z-index: 100;
}

body.silex-runtime.loaded:before {
    content: none
}

@keyframes spin {
    0% {
        transform: rotate(0deg);
    }
    100% {
        transform: rotate(360deg);
    }
}


/***********************\
    /Loader widget
\***********************/

/* ************ *\
  Drop Down Menu
\* ************ */
.silex-runtime .expandable {
    max-height: 70px;
}
.silex-runtime .expandable:hover {
    max-height: none;
}
.silex-runtime .expandable .level2 {
    transition: opacity 1s;
    opacity: 0;
    visibility: hidden;
}
.silex-runtime .expandable:hover .level2 {
    opacity: 1;
    visibility: visible;
}
.silex-runtime .expandable .text-element:hover {
    background-color: #FA8902;
    transition: background-color .5s;
}
.silex-runtime .expandable .normal {
    padding-top: 8px;
}

